import React from 'react';
import { motion } from 'framer-motion';

const experiences = [
  {
    role: "Software Engineer",
    company: "US Bank, Irving, Texas",
    period: "May 2024 - Mar 2025",
    description: `• Built enterprise-scale full-stack applications using Java, Spring Boot, React.js and Angular, 
• Developed secure RESTful APIs using Spring MVC,
• Integrated Spring Security with OAuth2 and JWT,
• Utilized Spring Core for dependency injection,
• Created dynamic UI components using React.js, TypeScript, and Bootstrap,
• Managed microservices with Docker and Kubernetes,
• Deployed applications on AWS (EC2, S3, RDS),
• Automated CI/CD pipelines via Jenkins,
• Collaborated in Agile teams using Jira,
• Achieved 90%+ test coverage with JUnit and Mockito.`
  },
  {
    role: "Software Engineer",
    company: "Cognizant Technology Solutions, Hyderabad, India (Client: AbbVie)",
    period: "August 2021 - July 2023",
    description: `• Designed scalable microservices using Spring Boot and MongoDB,
• Developed responsive web apps using React.js and Bootstrap,
• Integrated token-based authentication with Spring Security & JWT,
• Automated deployments using Docker and Kubernetes,
• Deployed and monitored applications on AWS,
• Wrote robust test cases using JUnit and Mockito,
• Maintained API documentation using OpenAPI (Swagger),
• Collaborated in Agile teams.`
  },
  {
    role: "Junior Software Engineer",
    company: "Cognizant Technology Solutions, Chennai, India (Client: Flipkart)",
    period: "Jan 2021 - August 2021",
    description: `• Developed REST APIs and backend modules using Spring Boot and Spring MVC,
• Built UI components with HTML5, CSS3, JavaScript, and Bootstrap,
• Optimized SQL queries using Hibernate and MySQL,
• Configured Log4j for detailed logging,
• Deployed applications on AWS,
• Conducted unit testing using JUnit,
• Collaborated on code merges using Git.`
  }
];

const Experience = () => {
  return (
    <section className="min-h-screen snap-start flex flex-col items-center bg-gradient-to-r from-indigo-100 to-blue-100 text-center px-4 py-16">
      <h2 className="text-4xl font-bold mb-10">Experience</h2>
      <div className="relative w-full max-w-4xl">
        {/* Vertical timeline line */}
        <div className="absolute left-1/2 transform -translate-x-1/2 h-full border-l-2 border-gray-300"></div>
        <div className="space-y-12">
          {experiences.map((exp, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: index * 0.2 }}
              className={`relative w-full md:w-1/2 p-6 bg-white rounded shadow 
                ${index % 2 === 0 ? "ml-auto" : "mr-auto"}`}
            >
              <h3 className="text-2xl font-bold text-gray-800">{exp.role}</h3>
              <p className="text-sm text-gray-500">{exp.company} &bull; {exp.period}</p>
              <p className="mt-3 text-gray-700 whitespace-pre-line">{exp.description}</p>
              {/* Timeline dot */}
              <div className="absolute top-4 -left-3 w-6 h-6 bg-indigo-600 rounded-full border-4 border-white"></div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Experience;
