import React from 'react';
import { Element } from 'react-scroll';
import { motion, AnimatePresence } from 'framer-motion';

const projectData = [
  {
    name: "Spam Email Detection",
    description: "A spam detection system using NLP techniques like TF-IDF, lemmatization, and ensemble learning. Achieved 98% accuracy on over 5,000 emails.",
    skills: [
      "Python", "Pandas", "NumPy", "scikit-learn", "SVM", "KNN", "Naïve Bayes",
      "Decision Tree", "Logistic Regression", "Random Forest", "AdaBoost",
      "Gradient Boosting", "XGBoost", "GridSearchCV", "TF-IDF", "Lemmatization",
      "Stopword Removal", "Tokenization", "Text Cleaning", "Confusion Matrix", "Voting Classifier"
    ]
  },
  {
    name: "Paraphrase Identification",
    description: "Built a transformer-based model using BERT, ELECTRA, and MobileBERT for semantic equivalence detection. Achieved 83%+ accuracy.",
    skills: [
      "Python", "TensorFlow", "Keras", "BERT", "DistilBERT", "ELECTRA", "MobileBERT",
      "Transformer Fine-tuning", "Tokenization", "Attention Masks", "Text Normalization"
    ]
  },
  {
    name: "Pension Management System",
    description: "Microservices-based system built using Java and Spring Boot, featuring secure APIs and responsive UI. Improved user satisfaction by 35%.",
    skills: [
      "Java", "Spring Boot", "Spring MVC", "Spring Core", "Spring Security", "JWT",
      "REST API", "JSP", "HTML", "CSS", "Bootstrap", "Swagger UI", "H2 Database"
    ]
  },
  {
    name: "Real-Time Facial Emotion Recognition",
    description: "Real-time CNN & ResEmoteNet system using OpenCV and FER2013 dataset. Included SE blocks, residuals, and real-time inference pipeline.",
    skills: [
      "Python", "TensorFlow", "Keras", "CNN", "ResEmoteNet", "Residual Connections",
      "SE Blocks", "OpenCV", "FER2013", "Data Augmentation", "Face Detection",
      "Normalization", "Hyperparameter Tuning", "Live Video Processing"
    ]
  }
];

const allTags = ["All", ...Array.from(new Set(projectData.flatMap(p => p.skills)))];

const Projects = () => {
  const [selectedTag, setSelectedTag] = React.useState("All");

  const filtered = selectedTag === "All"
    ? projectData
    : projectData.filter(p => p.skills.includes(selectedTag));

  return (
    // Wrap with Element and name it "projects" to match the Navbar link.
    <Element name="projects">
      <section className="min-h-screen snap-start flex flex-col items-center justify-start bg-gradient-to-r from-pink-50 to-purple-50 text-center px-4 py-16">
        <h2 className="text-4xl font-bold mb-6">Projects</h2>

        {/* Tag filtering buttons */}
        <div className="flex flex-wrap justify-center gap-2 mb-6">
          {allTags.map(tag => (
            <button
              key={tag}
              onClick={() => setSelectedTag(tag)}
              className={
                "px-4 py-1 rounded-full border " +
                (selectedTag === tag ? 'bg-purple-600 text-white' : 'bg-white text-purple-700 border-purple-300') +
                " hover:bg-purple-700 hover:text-white text-sm transition"
              }
            >
              {tag}
            </button>
          ))}
        </div>

        {/* 
          Changed the container for project cards:
          - Removed "overflow-x-auto" so cards can wrap.
          - Added "flex-wrap" to allow wrapping.
        */}
        <motion.div layout className="flex flex-wrap w-full gap-6 justify-center">
          <AnimatePresence>
            {filtered.map((proj) => (
              <motion.div
                key={proj.name}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -10 }}
                transition={{ duration: 0.3 }}
                // Use responsive width classes for the cards
                className="bg-white p-5 rounded shadow text-left w-full sm:w-1/2 md:w-1/3 h-[350px]"
              >
                <h3 className="text-xl font-bold text-purple-700 mb-1">{proj.name}</h3>
                <p className="text-sm text-gray-700 mb-2">{proj.description}</p>
                <p className="text-xs text-gray-600 mb-2">Key Skills:</p>
                <div className="flex flex-wrap gap-2 mt-1">
                  {proj.skills.map((skill, j) => (
                    <span key={j} className="bg-purple-100 text-purple-800 px-2 py-1 rounded text-xs">
                      {skill}
                    </span>
                  ))}
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </motion.div>
      </section>
    </Element>
  );
};

export default Projects;
