import React from 'react';
import { Element } from 'react-scroll';
import { motion, AnimatePresence } from 'framer-motion';

// Example data; replace with your actual projects
const projectData = [
  {
    name: "Halo Harbor",
    description:
      "A futuristic harbor simulation built with advanced AI algorithms for docking logistics and ship routing. Features real-time data and analytics.",
    skills: ["React", "AI", "Simulation", "Docker", "Node.js"]
  },
  {
    name: "SYCLConv",
    description:
      "This project implements depthwise separable convolution using SYCL and integrates it into the AI3 framework, aiming to enhance computational efficiency.",
    skills: ["C++", "SYCL", "AI3", "Computational Efficiency"]
  },
  {
    name: "ClassTrack",
    description:
      "A cross-platform class scheduling and management tool built in Flutter, integrated with Firebase for real-time updates and analytics.",
    skills: ["Flutter", "Firebase", "Dart", "Analytics"]
  },
  {
    name: "Smart Health Card",
    description:
      "IoT-based health card system that stores patient vitals in real-time, featuring encryption and easy mobile integration.",
    skills: ["IoT", "Encryption", "Mobile Integration", "React Native"]
  },
  {
    name: "WinForce ML",
    description:
      "A Windows-based tool for training and deploying machine learning models, featuring a user-friendly GUI and batch processing.",
    skills: ["Windows", "Machine Learning", "Python", "Batch Processing"]
  },
];

const allTags = ["All", ...Array.from(new Set(projectData.flatMap(p => p.skills)))];

const Projects = () => {
  const [selectedTag, setSelectedTag] = React.useState("All");

  // Filter projects by selected tag
  const filtered = selectedTag === "All"
    ? projectData
    : projectData.filter(p => p.skills.includes(selectedTag));

  return (
    <Element name="projects">
      <section className="min-h-screen snap-start flex flex-col items-center justify-start bg-gradient-to-r from-pink-50 to-purple-50 text-center px-4 py-16">
        <h2 className="text-4xl font-bold mb-6">Projects</h2>

        {/* Tag Filtering Buttons */}
        <div className="flex flex-wrap justify-center gap-2 mb-6">
          {allTags.map(tag => (
            <button
              key={tag}
              onClick={() => setSelectedTag(tag)}
              className={
                "px-4 py-1 rounded-full border " +
                (selectedTag === tag
                  ? 'bg-purple-600 text-white'
                  : 'bg-white text-purple-700 border-purple-300') +
                " hover:bg-purple-700 hover:text-white text-sm transition"
              }
            >
              {tag}
            </button>
          ))}
        </div>

        {/* Horizontal Scroll Container */}
        <motion.div
          layout
          // "flex" + no "flex-wrap" => single row
          // "overflow-x-auto" => horizontal scroll
          // "snap-x snap-mandatory" => optional snapping for a "carousel" feel
          className="flex overflow-x-auto gap-6 w-full pb-4 snap-x snap-mandatory"
        >
          <AnimatePresence>
            {filtered.map(proj => (
              <motion.div
                key={proj.name}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -10 }}
                transition={{ duration: 0.3 }}
                // flex-shrink-0 => don't shrink if container is too small
                // w-64 (16rem wide) => consistent card width
                // h-96 (24rem tall) => consistent card height
                // snap-start => each card "snaps" into view if scrolling with snapping
                className="
                  bg-white p-5 rounded shadow text-left
                  flex-shrink-0 w-64 h-96 snap-start
                "
              >
                <h3 className="text-xl font-bold text-purple-700 mb-1">{proj.name}</h3>
                <p className="text-sm text-gray-700 mb-2">{proj.description}</p>
                <p className="text-xs text-gray-600 mb-2">Key Skills:</p>
                <div className="flex flex-wrap gap-2 mt-1">
                  {proj.skills.map((skill, j) => (
                    <span
                      key={j}
                      className="bg-purple-100 text-purple-800 px-2 py-1 rounded text-xs"
                    >
                      {skill}
                    </span>
                  ))}
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </motion.div>
      </section>
    </Element>
  );
};

export default Projects;
