import React from 'react';
import { Element } from 'react-scroll';
import { motion, AnimatePresence } from 'framer-motion';
import { FaExternalLinkAlt } from 'react-icons/fa';

const certificationData = [
  {
    title: "Java Algorithms",
    subtitle: "Certificate of Completion",
    details: "Completed on Mar 31, 2023 • 2h 42min • LinkedIn Learning",
    link: "https://coursera.org/share/d54f921817a47ad4ffb1c7ea64fc575d"
  },
  {
    title: "Java: Data Structures",
    subtitle: "Certificate of Completion",
    details: "Completed on Mar 31, 2023 • 47min • LinkedIn Learning",
    link: "https://coursera.org/share/ddc5ae6ab3bc65d282aded9f071eaf32"
  },
  {
    title: "Learning Java 11",
    subtitle: "Certificate of Completion",
    details: "Completed on Mar 31, 2023 • 2h 39min • LinkedIn Learning",
    link: "https://coursera.org/share/fec434fc54e7bfde121e4c044c1a9cc8"
  },
  {
    title: "AI For Everyone",
    subtitle: "Certificate of Completion",
    details: "Completed on 08/29/2020 • Coursera",
    link: "https://coursera.org/verify/JTKVZP2CHEWR"
  },
  {
    title: "Python Functions, Files, and Dictionaries",
    subtitle: "Certificate of Completion",
    details: "Completed on 07/25/2020 • Coursera",
    link: "https://coursera.org/verify/JQR2F4HKSE5D"
  },
  {
    title: "Cisco Networking Academy - Python Programming",
    subtitle: "Statement of Achievement",
    details: "Completed on 4 Dec 2020 • OpenEDG Python Institute",
    link: "https://www.netacad.com" // Replace with the actual verify link if available.
  },
  {
    title: "AWS Certified Solutions Architect - Associate",
    subtitle: "Certificate",
    details: "AWS Certified Solutions Architect - Associate",
    link: "https://your-cert-link" // Update with your actual certificate URL.
  },
  {
    title: "Cognizant Certification - Diploma 1",
    subtitle: "Certificate of Completion",
    details: "Completed on 7/3/2022 • Angular 11 Assessment",
    link: "/certificates/Diploma_1a312c84-5951-4a7c-a6e1-3a00f97b97a3.pdf"
  },
  {
    title: "Cognizant Certification - Diploma 2",
    subtitle: "Certificate of Completion",
    details: "Completed on 6/25/2021 • Agile Software Development",
    link: "/certificates/Diploma_660ba7ce-e05d-49b7-94d2-03022a9f093f.pdf"
  }
];

const Certifications = () => {
  return (
    <Element name="certifications">
      <section className="min-h-screen snap-start flex flex-col items-center justify-start bg-gray-100 text-center px-4 py-16">
        <h2 className="text-4xl font-bold mb-6">Certifications</h2>
        <motion.div 
          layout 
          className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 w-full max-w-6xl pb-4"
        >
          <AnimatePresence>
            {certificationData.map((cert, index) => (
              <motion.a
                key={index}
                href={cert.link}
                target="_blank"
                rel="noopener noreferrer"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
                className="group bg-white rounded shadow p-6 flex flex-col items-center cursor-pointer hover:shadow-xl transition relative"
              >
                <h3 className="text-xl font-bold text-blue-700 mb-2">{cert.title}</h3>
                <p className="text-sm text-gray-600 mb-4">{cert.subtitle}</p>
                <p className="text-xs text-gray-500 mb-4">{cert.details}</p>
                <div className="flex items-center gap-2 text-blue-500 group-hover:text-blue-700">
                  <span className="font-semibold">View Certificate</span>
                  <FaExternalLinkAlt />
                </div>
              </motion.a>
            ))}
          </AnimatePresence>
        </motion.div>
      </section>
    </Element>
  );
};

export default Certifications;
