import React from 'react';
import { Element } from 'react-scroll';
import { motion } from 'framer-motion';

const experiences = [
  {
    role: "Software Engineer",
    company: "US Bank, Irving, Texas",
    period: "May 2024 - Mar 2025",
    description: `• Built enterprise-scale full-stack applications using Java, Spring Boot, React.js, and Angular.
• Developed secure RESTful APIs with Spring MVC and integrated Spring Security (OAuth2, JWT).
• Managed microservices with Docker & Kubernetes on AWS.
• Automated CI/CD pipelines via Jenkins with 90%+ test coverage (JUnit, Mockito).`
  },
  {
    role: "Software Engineer",
    company: "Cognizant Technology Solutions, Hyderabad (Client: AbbVie)",
    period: "August 2021 - July 2023",
    description: `• Designed scalable microservices using Spring Boot and MongoDB.
• Developed responsive web apps with React.js and Bootstrap.
• Integrated token-based authentication using Spring Security & JWT.
• Automated deployments using Docker & Kubernetes on AWS.`
  },
  {
    role: "Junior Software Engineer",
    company: "Cognizant Technology Solutions, Chennai (Client: Flipkart)",
    period: "Jan 2021 - August 2021",
    description: `• Developed REST APIs and backend modules using Spring Boot and Spring MVC.
• Built user interfaces with HTML5, CSS3, and Bootstrap.
• Optimized SQL queries using Hibernate & MySQL; implemented logging with Log4j.`
  }
];

const Experience = () => {
  return (
    <Element name="experience">
      <section className="min-h-screen snap-start flex flex-col items-center bg-gradient-to-r from-indigo-800 to-blue-700 text-white px-6 py-16">
        <h2 className="text-5xl font-extrabold mb-12">Experience</h2>
        <div className="relative w-full max-w-5xl">
          {/* Vertical timeline line */}
          <div className="absolute left-1/2 transform -translate-x-1/2 h-full border-l-4 border-white opacity-50"></div>
          <div className="space-y-16">
            {experiences.map((exp, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, x: index % 2 === 0 ? -100 : 100 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6, delay: index * 0.3 }}
                className={`relative w-full md:w-1/2 p-8 bg-white bg-opacity-20 rounded-xl shadow-xl 
                  ${index % 2 === 0 ? "ml-auto text-right" : "mr-auto text-left"}`}
              >
                <h3 className="text-2xl font-bold">{exp.role}</h3>
                <p className="text-sm opacity-80 mt-1">{exp.company} • {exp.period}</p>
                <p className="mt-4 text-white whitespace-pre-line">{exp.description}</p>
                {/* Timeline dot */}
                <div className="absolute top-4 -left-3 w-5 h-5 bg-white rounded-full border-4 border-indigo-800"></div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </Element>
  );
};

export default Experience;
