import React from 'react';
import { Element } from 'react-scroll';
import { motion } from 'framer-motion';

const educationData = [
  {
    institution: "University of Central Florida, Orlando, FL",
    degree: "Masters of Science in Computer Science",
    period: "May 2025",
    details: "Cumulative GPA: 3.9/4.0"
  },
  {
    institution: "Osmania University, Hyderabad",
    degree: "Bachelor of Engineering in Electronics and Communication Engineering",
    period: "May 2021",
    details: "Cumulative GPA: 7.6/10"
  }
];

const Education = () => {
  return (
    <Element name="education">
      <section className="min-h-screen snap-start flex flex-col items-center bg-gradient-to-r from-green-100 to-teal-200 text-center px-4 py-16">
        <h2 className="text-5xl font-extrabold text-gray-800 mb-12">Education</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-8 w-full max-w-4xl">
          {educationData.map((edu, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.2 }}
              className="bg-white p-8 rounded-xl shadow-xl hover:shadow-2xl transition transform hover:-translate-y-1"
            >
              <h3 className="text-2xl font-bold text-gray-800">{edu.institution}</h3>
              <p className="text-md text-gray-500 my-2">{edu.degree}</p>
              <p className="text-sm text-gray-400 mb-2">{edu.period}</p>
              <p className="text-gray-700">{edu.details}</p>
            </motion.div>
          ))}
        </div>
      </section>
    </Element>
  );
};

export default Education;
